@inproceedings{kawakita2014augmented,
  title = {Augmented {{TV}}: {{An}} Augmented Reality System for {{TV}} Programs beyond the {{TV}} Screen},
  booktitle = {2014 International Conference on Multimedia Computing and Systems ({{ICMCS}})},
  author = {Kawakita, Hiroyuki and Nakagawa, Toshio},
  year = {2014},
  month = apr,
  pages = {955--960},
  doi = {10.1109/ICMCS.2014.6911158},
  abstract = {TV Service using a mobile device as a second screen has been increasing. We propose a new TV system which is able to augment representation of TV programs beyond the TV screen. In the system, which we named augmented TV, since animated 3DCG content interlocked with TV programs is overlaid on live video from the mobile device camera in the mobile device screen by augmented reality techniques, the representation of having a TV character coming out of the screen can be provided. To achieve the representation giving such surprise or reality to the viewer, synchronization accuracy of the overlay display is required. A conventional synchronization method for multi-device or a visible light communication method does not meet the requirement of the accuracy. Therefore, we developed an accurate synchronization method and authoring environment of augmented TV content. We implemented augmented TV, and confirmed frame-accurate synchronization (synchronization error time is about 0.03 seconds or less). And we confirmed that the authoring environment is easy to produce augmented TV content using a video clip using a 3DCG character with TV program quality.}
}

@inproceedings{benko2007balloon,
  title = {Balloon {{Selection}}: {{A Multi-Finger Technique}} for {{Accurate Low-Fatigue 3D Selection}}},
  shorttitle = {Balloon {{Selection}}},
  booktitle = {2007 {{IEEE Symposium}} on {{3D User Interfaces}}},
  author = {Benko, Hrvoje and Feiner, Steven},
  year = {2007},
  month = mar,
  doi = {10.1109/3DUI.2007.340778},
  abstract = {Balloon selection is a 3D interaction technique that is modeled after the real world metaphor of manipulating a helium balloon attached to a string. Balloon selection allows for precise 3D selection in the volume above a tabletop surface by using multiple fingers on a multi-touch-sensitive surface. The 3DOF selection tasks is decomposed in part into a 2DOF positioning task performed by one finger on the tabletop in an absolute 2D Cartesian coordinate system and a 1DOF positioning task performed by another finger on the tabletop in a relative 2D polar coordinate system. We have evaluated balloon selection in a formal user study that compared it to two well-known interaction techniques for selecting a static 3D target: a 3DOF tracked wand and keyboard cursor keys. We found that balloon selection was significantly faster than using cursor keys and had a significantly lower error rate than the wand. The lower error rate appeared to result from the user's hands being supported by the tabletop surface, resulting in significantly reduced hand tremor and arm fatigue.}
}

@inproceedings{benko2004collaborativea,
  title = {Collaborative Mixed Reality Visualization of an Archaeological Excavation},
  booktitle = {Third {{IEEE}} and {{ACM International Symposium}} on {{Mixed}} and {{Augmented Reality}}},
  author = {Benko, H. and Ishak, E.W. and Feiner, S.},
  year = {2004},
  month = nov,
  pages = {132--140},
  doi = {10.1109/ISMAR.2004.23},
  abstract = {We present VITA (visual interaction tool for archaeology), an experimental collaborative mixed reality system for offsite visualization of an archaeological dig. Our system allows multiple users to visualize the dig site in a mixed reality environment in which tracked, see-through, head-worn displays are combined with a multi-user, multi-touch, projected table surface, a large screen display, and tracked hand-held displays. We focus on augmenting existing archaeological analysis methods with new ways to organize, visualize, and combine the standard 2D information available from an excavation (drawings, pictures, and notes) with textured, laser range-scanned 3D models of objects and the site itself. Users can combine speech, touch, and 3D hand gestures to interact multimodally with the environment. Preliminary user tests were conducted with archaeology researchers and students, and their feedback is presented here.}
}

