@inproceedings{kanaoka2024enhancing,
  title = {Enhancing Smishing Detection in {{AR}} Environments: {{Cross-device}} Solutions for Seamless Reality},
  booktitle = {2024 {{IEEE}} Conference on Virtual Reality and {{3D}} User Interfaces Abstracts and Workshops ({{VRW}})},
  author = {Kanaoka, Akira and Isohara, Takamasa},
  year = {2024},
  month = mar,
  pages = {565--572},
  doi = {10.1109/VRW62533.2024.00108},
  abstract = {Amidst the evolving landscape where cyber and physical realms seamlessly intertwine, smishing (SMS phishing) emerges as a so-phisticated security threat. This paper introduces a novel cross-device solution for detecting smishing attacks in augmented reality (AR) environments. Our method uses AR glasses to enhance user perception and interaction by analyzing images of URLs displayed across various devices, including smartphones, laptops, and digital displays. This approach aligns with the ``Seamless Reality'' concept, integrating physical and cyber space perception, cognition, and interaction. We detail the development of a prototype system and present findings from a user study that evaluates its effectiveness in a diverse range of AR settings. Our results underscore the potential of AR technologies in smishing detection, contributing to a safer interaction between virtual and real-world elements. The study also delves into aspects of AR display systems, user interface design, and the integration of haptic feedback, offering insights into the broader implications for seamless AR experiences.}
}

@inproceedings{butz1999enveloping,
  title = {Enveloping Users and Computers in a Collaborative {{3D}} Augmented Reality},
  booktitle = {Proceedings 2nd {{IEEE}} and {{ACM}} International Workshop on Augmented Reality ({{IWAR}}'99)},
  author = {Butz, A. and Hollerer, T. and Feiner, S. and MacIntyre, B. and Beshers, C.},
  year = {1999},
  month = oct,
  pages = {35--44},
  doi = {10.1109/IWAR.1999.803804},
  abstract = {We present EMMIE (Environment Management for Multiuser Information Environments), a prototype experimental user interface to a collaborative augmented environment. Users share a 3D virtual space and manipulate virtual objects that represent information to be discussed. We refer to EMMIE as a hybrid user interface because it combines a variety of different technologies and techniques, including virtual elements such as 3D widgets, and physical objects such as tracked displays and input devices. See-through head-worn displays overlay the virtual environment on the physical environment, visualizing the pervasive "virtual ether" within which all interaction occurs. Our prototype includes additional 2D and 3D displays, ranging from palm-sized to wall-sized, allowing the most appropriate one to be used for any task. Objects can be moved among displays (including across dimensionalities) through drag-and-drop. In analogy to 2D window managers, we describe a prototype implementation of a shared 3D environment manager that is distributed across displays, machines, and operating systems. We also discuss two methods we are exploring for handling information privacy in such an environment.}
}

