@inproceedings{elvezio2018hybrid,
  title = {Hybrid Uis for Music Exploration in {{AR}} and {{VR}}},
  booktitle = {2018 {{IEEE}} International Symposium on Mixed and Augmented Reality Adjunct ({{ISMAR-adjunct}})},
  author = {Elvezio, Carmine and Amelot, Pierre and Boyle, Robert and Wes, Catherine Ilona and Feiner, Steven},
  year = {2018},
  month = oct,
  pages = {411--412},
  doi = {10.1109/ISMAR-Adjunct.2018.00121},
  abstract = {We present hybrid user interfaces that facilitate interaction with music content in 3D, using a combination of 2D and 3D input and display devices. Participants will explore an online music library, some wearing AR or VR head-worn displays used alone or in conjunction with touch screens, and others using only touch screens. They will select genres, artists, albums and songs, interacting through a combination of 3D hand-tracking and 2D multi-touch technologies.}
}

@inproceedings{feiner1991hybrid,
  title = {Hybrid User Interfaces: {{Breeding}} Virtually Bigger Interfaces for Physically Smaller Computers},
  booktitle = {Proceedings of the 4th {{Annual ACM Symposium}} on {{User Interface Software}} and {{Technology}}, {{UIST}} 1991},
  author = {Feiner, Steven and Shamash, Ari},
  year = {1991},
  pages = {9--17},
  abstract = {While virtual worlds offer a compelling alternative to conventional interfaces, the technologies these systems currently use do not provide sufficient resolution and accuracy to support detailed work such as text editing. We describe a pragmatic approach to interface design that provides users with a large virtual world in which such high-resolution work can be performed. Our approach is based on combining heterogeneous display and interaction device technologies to produce a hybrid user interface. Display and interaction technologies that have relatively low resolution, but which cover a wide (visual and interactive) field are used to form an information surround. Display and interaction technologies that have relatively high resolution over a limited visual and interaction range are used to present concentrated information in one or more selected portions of the surround. These highresolution fields are embedded within the low-resolution surround by choosing and coordinating complementary devices that permit the user to see and interact with both simultaneously. This allows each embedded high-resolution interface to serve as a "sweet spot" within which information may be preferentially processed. We have developed a preliminary implementation, described in this paper, that uses a Reflection Technology Private Eye display and a Polhemus sensor to provide the secondary lowresolution surround, and a flat-panel display and mouse to provide the primary high-resolution interface.},
  isbn = {0-89791-451-1}
}

@inproceedings{vock2021idiar,
  title = {{{IDIAR}}: {{Augmented Reality Dashboards}} to {{Supervise Mobile Intervention Studies}}},
  booktitle = {{{ACM International Conference Proceeding Series}}},
  author = {Vock, Katja and Hubenschmid, Sebastian and Zagermann, Johannes and Butscher, Simon and Reiterer, Harald},
  year = {2021},
  pages = {248--259},
  doi = {10.1145/3473856.3473876},
  abstract = {Mobile intervention studies employ mobile devices to observe participants' behavior change over several weeks. Researchers regularly monitor high-dimensional data streams to ensure data quality and prevent data loss (e.g., missing engagement or malfunctions). The multitude of problem sources hampers possible automated detection of such irregularities - providing a use case for interactive dashboards. With the advent of untethered head-mounted AR devices, these dashboards can be placed anywhere in the user's physical environment, leveraging the available space and allowing for flexible information arrangement and natural navigation. In this work, we present the user-centered design and the evaluation of IDIAR: Interactive Dashboards in AR, combining a head-mounted display with the familiar interaction of a smartphone. A user study with 15 domain experts for mobile intervention studies shows that participants appreciated the multimodal interaction approach. Based on our findings, we provide implications for research and design of interactive dashboards in AR.},
  isbn = {978-1-4503-8645-6}
}

@inproceedings{zhang2021simultaneous,
  title = {Simultaneous Real Walking and Asymmetric Input in Virtual Reality with a Smartphone-Based Hybrid Interface},
  booktitle = {2021 {{IEEE}} International Symposium on Mixed and Augmented Reality Adjunct ({{ISMAR-adjunct}})},
  author = {Zhang, Li and He, Weiping and Cao, Zhiwei and Wang, Shuxia and Bai, Huidong and Billinghurst, Mark},
  year = {2021},
  month = oct,
  pages = {321--323},
  doi = {10.1109/ISMAR-Adjunct54149.2021.00072},
  abstract = {Compared to virtual navigation methods like joystick-based teleportation in Virtual Reality (VR), real walking enables more natural and realistic physical behaviors and better overall user experience. This paper presents a smartphone-based hybrid interface that combines a smartphone and a handheld controller, which enables real walking by viewing the physical environment and asymmetric 2D-3D input at the same time. The phone is virtualized in VR and streams a view of the real world to a collocated virtual screen, enabling users to avoid or remove physical obstacles. The touchscreen and the controller provide an asymmetric input choice for users to improve interaction efficiency in VR. We implemented a prototype system and conducted a pilot study to evaluate its usability.}
}

@article{grubert2023text,
  title = {Text Entry Performance and Situation Awareness of a Joint Optical See-through Head-Mounted Display and Smartphone System},
  author = {Grubert, Jens and Witzani, Lukas and Otte, Alexander and Gesslein, Travis and Kranz, Matthias and Kristensson, Per Ola},
  year = {2023},
  journal = {IEEE Transactions on Visualization and Computer Graphics},
  pages = {1--16},
  issn = {1941-0506},
  doi = {10.1109/TVCG.2023.3309316},
  abstract = {Optical see-through head-mounted displays (OST HMDs) are a popular output medium for mobile Augmented Reality (AR) applications. To date, they lack efficient text entry techniques. Smartphones are a major text entry medium in mobile contexts but attentional demands can contribute to accidents while typing on the go. Mobile multi-display ecologies, such as combined OST HMD-smartphone systems, promise performance and situation awareness benefits over single-device use. We study the joint performance of text entry on mobile phones with text output on optical see-through head-mounted displays. A series of five experiments with a total of 86 participants indicate that, as of today, the challenges in such a joint interactive system outweigh the potential benefits.}
}

