@inproceedings{gonzalez2024xdtk,
  title = {{{XDTK}}: A Cross-Device Toolkit for Input \& Interaction in {{XR}}},
  booktitle = {2024 {{IEEE}} Conference on Virtual Reality and {{3D}} User Interfaces Abstracts and Workshops ({{VRW}})},
  author = {Gonzalez, Eric J. and Patel, Khushman and Ahuja, Karan and {Gonzalez-Franco}, Mar},
  year = {2024},
  month = mar,
  pages = {467--470},
  doi = {10.1109/VRW62533.2024.00092},
  abstract = {We present XDTK, an open-source Unity/Android toolkit for proto-typing multi-device interactions in extended reality (XR). With the Unity package and Android app provided in XDTK, data from any number of devices (phones, tablets, or wearables) can be streamed to and surfaced within a Unity-based XR application. ARCore-supported device also provide self-tracked pose data. Devices on the same local network are automatically discovered by the Unity server and their inputs are routed using a custom event framework. We designed XDTK to be modular and easily extendable to enable fast, simple, and effective prototyping of multi-device experiences by both researchers and developers.}
}
